          <?php To_f_Chapter_v1( $Sc_g_Text_replace_ary, $Sc_g_Text_replace_preg_ary, '          ', 'invis',
            'OM:nSOSp:Home:Forschungsstand',
              'Ein nie vollständiger Überblick', 'Sc_f_Paragraph',
                array(
